import React, { useState, useEffect, useContext } from "react";
import MajorDataService from "../../services/MajorRequirements.js";
import SearchBar from "./SearchBar";
import MajorNames from "../../data/data.json";
import UserContext from "../../contexts/UserContext";
import "./index.scss";
import { makeStyles } from "@material-ui/styles";
import { CardContent, Card, Typography, Grid, Checkbox } from "@mui/material";
import UserCourseService from "../../services/UserCourseList.js";
import UserCourseList from "../../services/UserCourseList.js";

const Home = () => {
  const { isLoading, isAuthenticated, courseList } = useContext(UserContext);

  const initialMajorState = {
    majorName: "",
    majorCode: "",
    majorComponentFamilies: [],
  };

  const [major, setMajor] = useState(initialMajorState);

  const [checkedState, setCheckedState] = useState(courseList);

  const handleOnChange = (checkedName) => {
    // const updatedCheckedState = checkedState.map((item, index) =>
    //   index === position ? !item : item
    // );

    const updatedCheckedState = checkedState?.includes(checkedName)
      ? checkedState?.filter((name) => name !== checkedName)
      : [...(checkedState ?? []), checkedName];
    let difference = updatedCheckedState.filter(
      (x) => !checkedState.includes(x)
    );
    if (difference.length > 0) {
      UserCourseList.add(checkedName);
    } else {
      console.log(checkedName);
      UserCourseList.delete(checkedName);
    }
    console.log(difference);
    setCheckedState(updatedCheckedState);

    // if (!checkedState[position]) {
    //   UserCourseList.add(name);
    // } else {
    //   UserCourseList.delete(name);
    // }
    return updatedCheckedState;
  };

  const useStyles = makeStyles({
    gridContainer: {
      paddingLeft: "85px",
      paddingRight: "85px",
    },
    card: {
      minWidth: 200,
      borderRadius: "16px",
      boxShadow: "0 4px 30px rgba(0, 0, 0, 0.1)",
      backdropFlter: "blur(5px)",
      border: "1px solid rgba(255, 255, 255, 0.3)",
      marginBottom: "10px",
      background: "rgba(255, 255, 255, 0.2)",
    },
    ComponentFamilyTitle: {
      fontSize: "18px !important",
      fontStyle: "bold",
    },
    CourseTitle: {
      fontSize: "12px !important",
      fontStyle: "bold",
    },
    ComponentTitle: {
      fontSize: "12px !important",
      fontStyle: "bold",
    },
    pos: {
      marginBottom: 12,
    },
    checkbox: {
      // float: "right",
      flat: "top",
    },
  });

  const classes = useStyles();
  useEffect(() => {
    getMajor();
  }, []);

  const getMajor = async (query, by) => {
    MajorDataService.find(query, by)
      .then((response) => {
        setMajor(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  var checkIndex = 0;
  const renderCourseRequirements = (RequiredCourses) => {
    return (
      <Grid
        container
        spacing={0}
        direction="column"
        alignItems="center"
        justifyContent="center"
      >
        {RequiredCourses.map((course, index) => {
          var Index = checkIndex;
          checkIndex = checkIndex + 1;
          return (
            <Grid key={index} item xs={12} sm={6} md={4}>
              <Card className={classes.card} variant="outlined">
                <CardContent>
                  <Typography
                    className={classes.CourseTitle}
                    color="textSecondary"
                    gutterBottom
                  >
                    {course}
                  </Typography>
                  <Checkbox
                    id={`custom-checkbox-${Index}`}
                    name={course}
                    value={course}
                    className={classes.checkbox}
                    checked={checkedState.includes(course)}
                    onChange={() => handleOnChange(course)}
                  />
                </CardContent>
              </Card>
            </Grid>
          );
        })}
      </Grid>
    );
  };
  const renderComponents = (components) => {
    return (
      <Grid
        container
        spacing={0}
        direction="column"
        alignItems="center"
        justifyContent="center"
      >
        {components.map((component, index) => {
          return (
            <Grid
              container
              spacing={0}
              direction="column"
              alignItems="stretch"
              justifyContent="center"
              key={index}
              item
              xs={12}
              sm={6}
              md={4}
            >
              <Card className={classes.card} variant="outlined">
                <CardContent>
                  <Typography
                    className={classes.ComponentTitle}
                    color="textSecondary"
                    gutterBottom
                  >
                    {component.component_name}
                  </Typography>
                  <Typography className={classes.pos} color="textSecondary">
                    Required # of Courses: {component.required_num_courses}
                  </Typography>
                  {renderCourseRequirements(component.course_list)}
                </CardContent>
              </Card>
            </Grid>
          );
        })}
      </Grid>
    );
  };

  const renderComponentFamilies = (componentFamilies) => {
    return (
      <Grid container spacing={4} className={classes.item}>
        {componentFamilies.map((componentFamily, index) => {
          if (componentFamily.component_list.length > 1)
            return (
              <Grid key={index} xs={8} sm={6} md={4}>
                <Card className={classes.card} variant="outlined">
                  <CardContent>
                    <Typography
                      className={classes.ComponentFamilyTitle}
                      color="textSecondary"
                      gutterBottom
                    >
                      {componentFamily.component_family_name}
                    </Typography>
                    <Typography className={classes.pos} color="textSecondary">
                      Required # of Components :{" "}
                      {componentFamily.required_num_components}
                    </Typography>
                    {renderComponents(componentFamily.component_list)}
                  </CardContent>
                </Card>
              </Grid>
            );
          return (
            <>
              <Grid key={index} item xs={12} sm={6} md={4}>
                {renderComponents(componentFamily.component_list)}
              </Grid>
            </>
          );
        })}
      </Grid>
    );
  };
  return (
    <div className="container home">
      <SearchBar
        placeholder="Enter Major Name ..."
        data={MajorNames}
        onChange={(value) => getMajor(value, "name")}
      />
      <div className="mui-grid">
        {Array.isArray(major.majorComponentFamilies) &&
        major.majorComponentFamilies.length > 0 ? (
          <>
            <h1>
              {major.majorName} ({major.majorCode})
            </h1>
            {renderComponentFamilies(major.majorComponentFamilies)}
          </>
        ) : (
          <></>
        )}
      </div>
    </div>
  );
};

export default Home;
