import React, { useState, useEffect, useContext } from "react";
import MajorDataService from "../../services/MajorRequirements.js";
import SearchBar from "../SearchBar";
import MajorNames from "../../data/data.json";
import UserContext from "../../contexts/UserContext";
import "./index.scss";
import { menuItems } from "../../menuitems.js";
import Box from "@mui/material/Box";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import { makeStyles } from "@material-ui/styles";

const Home = () => {
  const { checkContext } = useContext(UserContext);
  const [major, setMajor] = useState(menuItems);
  const [searchName, setSearchName] = useState("");
  const [checked, setChecked] = useState([]);

  const useStyles = makeStyles({
    gridContainer: {
      paddingLeft: "100px",
      paddingRight: "100px",
    },
    root: {
      minWidth: 200,
    },
    bullet: {
      display: "inline-block",
      margin: "0 2px",
      transform: "scale(0.8)",
    },
    title: {
      fontSize: 14,
    },
    pos: {
      marginBottom: 12,
    },
  });

  const classes = useStyles();
  useEffect(() => {
    getMajor();
  }, []);

  const onChangeSearchName = (e) => {
    const searchName = e.target.value;
    setSearchName(searchName);
  };

  const getMajor = async (query, by) => {
    MajorDataService.find(query, by)
      .then((response) => {
        setMajor(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const renderCourseRequirements = (courseList) => {
    return (
      <div>
        {courseList.map((course, index) => {
          return (
            <div key={index}>
              <input value={course} type="checkbox" />
              <span>{course}</span>
            </div>
          );
        })}
      </div>
    );
  };

  const renderComponents = (components) => {
    return (
      <div>
        {components.map((component, index) => {
          return (
            <>
              <h2>{component.component_name}</h2>
              <p>
                Required Number of Courses: {component.required_num_courses}
              </p>
              {renderCourseRequirements(component.course_list)}
            </>
          );
        })}
      </div>
    );
  };

  const renderComponentFamilies = (componentFamilies) => {
    return (
      <div className="component-family">
        <Grid
          container
          spacing={4}
          className={classes.gridContainer}
          justify="center"
        >
          {componentFamilies.map((componentFamily, index) => {
            return (
              <Card className={classes.root} variant="outlined" key={index}>
                <CardContent>
                  <Typography sx={{ fontSize: 14 }} color="black" gutterBottom>
                    {componentFamily.component_family_name}
                  </Typography>
                </CardContent>
              </Card>
            );
          })}
        </Grid>
      </div>
    );
  };

  return (
    <div className="container major-requirements-page">
      <SearchBar
        placeholder="Enter Major Name ..."
        data={BookData}
        onChange={(value) => getMajor(value, "name")}
      />

      {Array.isArray(major.majorComponentFamilies) &&
      major.majorComponentFamilies.length > 0 ? (
        <div className="text-area">
          <h1>
            {major.majorName} ({major.majorCode})
          </h1>
          <div className="component-family">
            {renderComponentFamilies(major.majorComponentFamilies)}
          </div>
        </div>
      ) : (
        <></>
      )}
    </div>
  );
};

export default Home;
